mapper.select:
select
from ZYB_YRDW where 1=1
and EMPLOYER_NAME = #{employerName,jdbcType=VARCHAR}

select
from ZYB_YRDW where EMPLOYER_CODE in
#{item}

select
from ZYB_YRDW where EMPLOYER_NAME in
#{item}

mapper.insert:
insert into ZYB_YRDW (CREDIT_CODE, EMPLOYER_CODE, EMPLOYER_NAME, EMPLOYER_DESC, AREA_STANDARD, AREA_ADDRESS, ECONOMIC_CODE, INDUSTRY_CATE_CODE, ENTERPRISE_CODE, SECOND_EMPLOYER_CODE, SECOND_EMPLOYER_NAME, POST_ADDRESS, ZIP_CODE, CONTACT_PERSON, CONTACT_PHONE, MONITOR_ORG_CODE, MONITOR_ORG_NAME, REMARKS, SBBZ, SBYY, SBSJ, LOGSJ ) (
select #{item.creditCode,jdbcType=VARCHAR}, #{item.employerCode,jdbcType=VARCHAR}, #{item.employerName,jdbcType=VARCHAR}, #{item.employerDesc,jdbcType=VARCHAR}, #{item.areaStandard,jdbcType=VARCHAR}, #{item.areaAddress,jdbcType=VARCHAR}, #{item.economicCode,jdbcType=VARCHAR}, #{item.industryCateCode,jdbcType=VARCHAR}, #{item.enterpriseCode,jdbcType=VARCHAR}, #{item.secondEmployerCode,jdbcType=VARCHAR}, #{item.secondEmployerName,jdbcType=VARCHAR}, #{item.postAddress,jdbcType=VARCHAR}, #{item.zipCode,jdbcType=VARCHAR}, #{item.contactPerson,jdbcType=VARCHAR}, #{item.contactPhone,jdbcType=VARCHAR}, #{item.monitorOrgCode,jdbcType=VARCHAR}, #{item.monitorOrgName,jdbcType=VARCHAR}, #{item.remarks,jdbcType=VARCHAR}, #{item.sbbz,jdbcType=VARCHAR}, #{item.sbyy,jdbcType=VARCHAR}, #{item.sbsj,jdbcType=TIMESTAMP}, #{item.logsj,jdbcType=TIMESTAMP} from dual
)
